import Keypad from './components/Keypad';
import { useState } from 'react';

function App() {
  const[result,setResult] = useState("");
  const[result1,setResult1] = useState("");
  const handleClick = (e) =>
  {
      setResult(result.concat(e.target.name));
      if(result.slice(0,1) === '/' || '+')
      {
        setResult1("");
      }
      else
      {
        setResult1(result.concat(e.target.name));
      }

  }

  const clear = () => {
      setResult1("");
  }
  const backspace = () => {
      setResult1(result1.slice(0,-1));
  }
  const calculate = () =>
  {
    try{
      setResult1(eval(result1).toString());
      console.log(result);
    }catch(err){
      setResult1("");
    }

  }

  return (
    <div className="container">
      <Switcher/>
      <Screen result1={result1}/>
      <Keypad handleClick={handleClick} clear={clear} backspace={backspace} calculate={calculate}/>
    </div>
  );
}

export default App;
