import Switcher from './components/Switcher';
import Screen  from './components/Screen';
import Keypad from './components/Keypad';
import { useState } from 'react';

function App() {
  const[result,setResult] = useState("");
  const[themeBlue,setThemeBlue] = useState("");
  const[themeWhite,setThemeWhite] = useState("");
  const[themePurple,setThemePurple] = useState("");
  const handleClick = (e) =>
  {
      setResult(result.concat(e.target.name));
      if(result.slice(0,1) === '/' || '+')
      {
        console.log(result.slice)
        setResult(result.concat(e.target.name));
      }
      else
      {
        setResult(result.concat(e.target.name));
      }

  }
  const clear = () => {
      setResult("");
  }
  const backspace = () => {
      setResult(result.slice(0,-1));
  }
  const calculate = () =>
  {
    try{
      setResult(eval(result).toString());
      console.log(result);
    }catch(err){
      setResult("");
    }

  }
  const theme1 = (e) =>{
    if(e.target.checked){
      document.documentElement.setAttribute("data-theme","theme-1")
      localStorage.setItem("data-theme ","theme-1");
      setThemeBlue("true");
      setThemeWhite("false");
    }
  }
  const theme2 = (e) =>{
    if(e.target.checked){
      document.documentElement.setAttribute("data-theme","theme-2")
      localStorage.setItem("data-theme ","theme-2");
      setThemeWhite("true");
    }
  }
  const theme3 = (e) =>{
    if(e.target.checked){
      document.documentElement.setAttribute("data-theme","theme-3")
      localStorage.setItem("data-theme ","theme-3");
      setThemePurple("true");
    }
  }

  return (
    <div className="container">
      <Switcher theme1={theme1} themeBlue={themeBlue} theme2={theme2} themeWhite={themeWhite} theme3={theme3} themePurple={themePurple} />
      <Screen result={result}/>
      <Keypad handleClick={handleClick} clear={clear} backspace={backspace} calculate={calculate}/>
    </div>
  );
}

export default App;
